Rules.InvariantScript:
Set(
    global_email_usuario_corrente,
    UsuáriosdoOffice365.MyProfileV2().mail
    //"nelson.junior@harald.com.br"
);
If(
    IsBlankOrError(global_email_usuario_corrente),
    Set(
        global_email_usuario_corrente,
        UsuáriosdoOffice365.MyProfile().Mail
    )
);
If(
    IsBlankOrError(global_email_usuario_corrente),
    Set(
        global_email_usuario_corrente,
        Lower(User().Email)
    )
);
ClearCollect(
    colecao_cargos_conexao_sinergia,
    '[PPR] - Cargos Meta Conexão e Sinergia'
);
ClearCollect(
    colecao_colaboradores_sob_gestao,
    Filter(
        'Cadastro de Colaboradores',
        EmailGestor = global_email_usuario_corrente
    )
);
If(
    CountRows(colecao_colaboradores_sob_gestao) > 0,
    Set(
        global_permite_acesso_gestor,
        true
    ),
    Set(
        global_permite_acesso_gestor,
        false
    )
);
//Acesso Gestao Pessoas
ClearCollect(
    colecao_colaboradores_gestao_pessoas,
    Filter(
        'Avaliação de Desempenho - Usuários Gestão de Pessoas',
        EmailUsuario = global_email_usuario_corrente
    )
);
If(
    CountRows(colecao_colaboradores_gestao_pessoas) > 0,
    Set(
        global_is_gestao_pessoas,
        true
    ),
    Set(
        global_is_gestao_pessoas,
        false
    )
)
//ClearCollect(
//    colecao_usuarios;
//    AzureAD.GetGroupMembers("ded6ae2c-da31-460c-9d1f-5e9383e4914d").value
//);;

If(
    IsMatch(
        Self.Selected.Value,
        "1",
        Contains
    ),
    //tema 1
    Set(
        global_geral_configuration_color,
        RGBA(
            255,
            255,
            255,
            1
        )
    );
    Set(
        global_geral_configuration_bg,
        bg1
    ),
    IsMatch(
        Self.Selected.Value,
        "2",
        Contains
    ),
    //tema 2
    Set(
        global_geral_configuration_color,
        RGBA(
            0,
            0,
            0,
            1
        )
    );
    Set(
        global_geral_configuration_bg,
        bg6
    ),
    IsMatch(
        Self.Selected.Value,
        "3",
        Contains
    ),
    //tema 3
    Set(
        global_geral_configuration_color,
        RGBA(
            180,
            0,
            0,
            1
        )
    );
    Set(
        global_geral_configuration_bg,
        Blank()
    )
)

Set(
    global_is_acesso_relatorio,
    false
);
Set(
    global_acesso_corrente,
    const_acesso_corrente_colaborador
);
Set(
    global_email_gestor,
    LookUp(
        'Cadastro de Colaboradores',
        EmailColaborador = global_email_usuario_corrente
    ).EmailGestor
);
If(
    IsBlankOrError(global_email_gestor),
    Notify(
        LookUp(
            colecao_mensagens_capa,
            idioma = global_idioma
        ).gestor_nao_localizado,
        Error
    ),
    Navigate(
        Screen_Home,
        ScreenTransition.Fade
    )
)

Set(
    global_is_acesso_relatorio,
    false
);
Set(
    global_acesso_corrente,
    const_acesso_corrente_gestor
);
Set(
    global_email_gestor,
    global_email_usuario_corrente
);
Navigate(
    Screen_Home,
    ScreenTransition.Fade
)

Set(
    global_is_acesso_relatorio,
    false
);
Set(
    global_acesso_corrente,
    const_acesso_corrente_colaborador
);
Set(
    global_email_gestor,
    LookUp(
        'Cadastro de Colaboradores',
        EmailColaborador = global_email_usuario_corrente
    ).EmailGestor
);
If(
    IsBlankOrError(global_email_gestor),
    Notify(
        LookUp(
            colecao_mensagens_capa,
            idioma = global_idioma
        ).gestor_nao_localizado,
        Error
    ),
    Navigate(
        Screen_Home,
        ScreenTransition.Fade
    )
)

Set(
    global_is_acesso_relatorio,
    false
);
Set(
    global_acesso_corrente,
    const_acesso_corrente_gestor
);
Set(
    global_email_gestor,
    global_email_usuario_corrente
);
Navigate(
    Screen_Home,
    ScreenTransition.Fade
)

Set(
    global_modo_gestao_pessoas,
    true
);
Navigate(
    relatorio,
    ScreenTransition.Fade
)
