            ThisRecord.DisplayName <> global_colaborador_nome,
            Collect(
                local_colecao_cargo,
                {
                    Email: colecao_colaboradores_sob_gestao_AD[@Mail],
                    Cargo: LookUp(
                        'Cadastro de Colaboradores',
                        EmailColaborador = colecao_colaboradores_sob_gestao_AD[@Mail]
                    ).Cargo
                }
            );
            Collect(
                local_colecao_categoria,
                {
                    Email: colecao_colaboradores_sob_gestao_AD[@Mail],
                    Categoria: LookUp(
                        '[PPR] - Lista de Cargos',
                        Título = LookUp(
